import React from "react";
import { Link } from "react-router-dom";

const StudentView = (props) => {
  const { student } = props;

  if (!student || !student.id) {
    return <p>Loading student…</p>;
  }

  // 1. 默认头像：public/student.png
  const imgSrc = student.imageUrl?.trim() || '/student.png';

  // 2. GPA 转成数字并格式化
