/*==================================================
NewStudentView.js

The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display the new student page.
================================================== */
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import React from 'react';

// Create styling for the input form
const useStyles = makeStyles( () => ({
  formContainer:{  
    width: '500px',
    backgroundColor: '#f0f0f5',
    borderRadius: '5px',
    margin: 'auto',
  },
  customizeAppBar:{
    backgroundColor: '#11153e',
    shadows: ['none'],
  },
  formTitle:{
    backgroundColor:'#c5c8d6',
    marginBottom: '15px',
    textAlign: 'center',
    borderRadius: '5px 5px 0px 0px',
    padding: '3px'
  },
  field: {
    width: '90%',
    padding: '8px',
    margin: '4px 0 12px 0',
    borderRadius: '4px',
    border: '1px solid #ccc',
    fontSize: '1rem'
  },
  label: {
    fontWeight: 'bold',
    color: '#11153e',
    display: 'block',
    textAlign: 'left',
    marginLeft: '5%'
  },
  submitWrapper: {
    textAlign: 'center',
    marginTop: '1rem'
  }
}));

const NewStudentView = (props) => {
  const {handleChange, handleSubmit, firstname, lastname, email, imageUrl, gpa, campusId } = props;
  const classes = useStyles();

  // Render a New Student view with an input form
  return (
    <div>
      <h1>New Student</h1>
      <div className={classes.formContainer}>
        <div className={classes.formTitle}>
          <Typography
            style={{fontWeight: 'bold',
              fontFamily: 'Courier, sans-serif',
              fontSize: '20px',
              color: '#11153e'
            }}
          >
            Add a Student
          </Typography>
        </div>
        <form onSubmit={handleSubmit}>
          <label className={classes.label} htmlFor="firstname">
            First Name:
          </label>
          <input
            id="firstname"
            className={classes.field}
            type="text"
            name="firstname"
            value={firstname}
            onChange={handleChange}
            required
          />

          <label className={classes.label} htmlFor="lastname">
            Last Name:
          </label>
          <input
            id="lastname"
            className={classes.field}
            type="text"
            name="lastname"
            value={lastname}
            onChange={handleChange}
            required
          />

          <label className={classes.label} htmlFor="email">
            Email:
          </label>
          <input
            id="email"
            className={classes.field}
            type="email"
            name="email"
            value={email}
            onChange={handleChange}
            required
          />

          <label className={classes.label} htmlFor="imageUrl">
            Image URL:
          </label>
          <input
            id="imageUrl"
            className={classes.field}
            type="text"
            name="imageUrl"
            value={imageUrl}
            onChange={handleChange}
          />

          <label className={classes.label} htmlFor="gpa">
            GPA:
          </label>
          <input
            id="gpa"
            className={classes.field}
            type="number"
            name="gpa"
            value={gpa}
            onChange={handleChange}
            step="0.1"
            min="0"
            max="4"
          />

          <label className={classes.label} htmlFor="campusId">
            Campus ID:
          </label>
          <input
            id="campusId"
            className={classes.field}
            type="number"
            name="campusId"
            value={campusId}
            onChange={handleChange}
          />

          <div className={classes.submitWrapper}>
            <Button variant="contained" color="primary" type="submit">
              Submit
            </Button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default NewStudentView;
