// src/components/containers/EditStudentContainer.js
import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom';
import Header from './Header';
import EditStudentView from '../views/EditStudentView';
import { fetchStudentThunk, editStudentThunk } from '../../store/thunks';

class EditStudentContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstname: '',
      lastname: '',
      email: '',
      imageUrl: '',
      gpa: '',
      campusId: '',
      redirect: false,
      redirectId: null
    };
  }

  async componentDidMount() {
    const { id } = this.props.match.params;
    const student = await this.props.fetchStudent(id);
    this.setState({
      firstname: student.firstname || '',
      lastname: student.lastname || '',
      email: student.email || '',
      imageUrl: student.imageUrl || '',
      gpa: student.gpa || '',
      campusId: student.campusId || ''
    });
  }

  handleChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  }

  handleSubmit = async e => {
    e.preventDefault();
    const { id } = this.props.match.params;
    const updated = {
      id,
      ...this.state
    };
    await this.props.editStudent(id, updated);
    this.setState({ redirect: true, redirectId: id });
  }

  render() {
    if (this.state.redirect) {
      return <Redirect to={`/student/${this.state.redirectId}`} />;
    }
    return (
      <div>
        <Header />
        <EditStudentView
          campuses={this.props.campuses}
          {...this.state}
          handleChange={this.handleChange}
          handleSubmit={this.handleSubmit}
        />
      </div>
    );
  }
}

const mapState = state => ({
  campuses: state.allCampuses
});

const mapDispatch = dispatch => ({
  fetchStudent: id => dispatch(fetchStudentThunk(id)),
  editStudent: (id, student) => dispatch(editStudentThunk(id, student))
});

export default connect(mapState, mapDispatch)(EditStudentContainer);
