/*==================================================
StudentView.js

The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display the single student view page.
================================================== */
import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Button } from "@material-ui/core";
import { Link, useHistory } from "react-router-dom";

const useStyles = makeStyles(() => ({
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    padding: "2rem",
  },
  name: {
    fontSize: "2rem",
    marginBottom: "1rem",
  },
  avatar: {
    width: 160,
    height: 160,
    borderRadius: "50%",
    objectFit: "cover",
    marginBottom: "1.5rem",
    backgroundColor: "#eee",
  },
  info: {
    textAlign: "center",
    marginBottom: "0.5rem",
  },
  label: {
    fontWeight: "bold",
    marginRight: "0.3rem",
  },
  buttonRow: {
    marginTop: "2rem",
    display: "flex",
    gap: "1rem",
  }
}));

const StudentView = ({ student, deleteStudent }) => {
  const classes = useStyles();
  const history = useHistory();

  if (!student || !student.id) {
    return <p className={classes.container}>Loading studentâ€¦</p>;
  }

  const {
    id,
    firstname,
    lastname,
    email,
    imageUrl,
    gpa,
    campus,
  } = student;

  // 1. Determine avatar URL (fall back to public/student.png)
  const imgSrc = imageUrl?.trim() || "/student.png";

  // 2. Parse and format GPA
  const gpaNum = parseFloat(gpa);
  const gpaDisplay = Number.isFinite(gpaNum) ? gpaNum.toFixed(2) : "N/A";

  // Render a single Student view 
  return (
    <div style={{ padding: "1rem" }}>
      <h1>
        {firstname} {lastname}
      </h1>

      {/* Avatar */}
      <img
        src={imgSrc}
        alt={`${firstname} ${lastname}`}
        style={{
          width: 200,
          height: 200,
          objectFit: "cover",
          borderRadius: "50%",
          display: "block",
          marginBottom: "1rem"
        }}
      />

      {/* Email */}
      <p>
        <strong>Email:</strong> {email}
      </p>

      {/* GPA */}
      <p>
        <strong>GPA:</strong> {gpaDisplay}
      </p>

      {/* Associated Campus */}
      <p>
        <strong>Campus:</strong>{" "}
        {campus ? (
          <Link to={`/campus/${campus.id}`}>{campus.name}</Link>
        ) : (
          "Not enrolled"
        )}
      </p>
    </div>
  );
};

export default StudentView;
